     id : app id ,
     debug :true,
     ads :{
         video :{
             zone :  zone id ,
             v4vc : false
        },
         v4vc :{
             zone :  zone id ,
             v4vc : true,
             pre_popup  : true,
             post_popup : true
        }
    }
}

